const hundredPercent = "width: 98%;max-width: 98%;margin: 1%;";
const hundredPercentWFloat = "width: 98%;max-width: 98%;margin: 1%; float: left;";
export default {
  data() {
    return {
      valuesRegister: {
        nombre: "",
        tipoDocumento: "",
        documento: "",
        contacto: "",
        correo: "",
        representante_legal: "",
        direccion: "",
        fechaCorte: "",
        fechaRemision: "",
        rol: 8,
        userSearchSelected: [],
        costos: []
      },
      preServiciosSelected: 0,
      menu: false,
      serviciosDefault: [],
      schemaRegisterUsuario: [{
        id: "nombre",
        component: "VTextField",
        label: "Nombre",
        style: fiftyPercentWFloat
      },
      //Id Tipo Documento
      {}, {
        id: "documento",
        component: "VTextField",
        label: "Documento",
        style: thirtyPercentWFloat
      }, {
        id: "contacto",
        component: "VTextField",
        label: "Teléfono",
        style: thirtytreePercentWFloat
      }, {
        id: "correo",
        component: "VTextField",
        label: "Correo",
        style: thirtytreePercentWFloat
      }, {
        id: "cargo",
        component: "VTextField",
        label: "Cargo",
        style: thirtytreePercentWFloat
      },
      //Info contacto
      {},
      //Tipo de rol
      {}],
      schemaRegister: [{
        id: "nombre",
        component: "VTextField",
        placeholder: "Razón Social",
        style: fiftyPercentWFloat
      },
      //Id Tipo Documento
      {}, {
        id: "documento",
        component: "VTextField",
        placeholder: "No. Cedula / NIT",
        style: thirtyPercent
      }, {
        id: "contacto",
        component: "VTextField",
        placeholder: "Teléfono",
        style: thirtytreePercentWFloat
      }, {
        id: "correo",
        component: "VTextField",
        placeholder: "Correo",
        style: thirtytreePercentWFloat
      }, {
        id: "representante_legal",
